CREATE TABLE IF NOT EXISTS `guestbook_responses` (`id` int(11) NOT NULL auto_increment, `type_id` int(11) NOT NULL, `account_id` int(11) NOT NULL default '0', `pending` int(1) NOT NULL default '0', `status` int(1) NOT NULL default '1', `when_created` datetime NOT NULL, `when_expired` datetime NOT NULL, `ip_address` varchar(46) NOT NULL, `slot_1` text NOT NULL COMMENT 'title', `slot_2` text NOT NULL, `slot_3` text NOT NULL, `slot_4` text NOT NULL, `slot_5` text NOT NULL, `slot_6` text NOT NULL, `slot_7` text NOT NULL, `slot_8` text NOT NULL, `slot_9` text NOT NULL, `slot_10` text NOT NULL, `slot_11` text NOT NULL, `slot_12` text NOT NULL, `slot_13` text NOT NULL, `slot_14` text NOT NULL, `slot_15` text NOT NULL, `slot_16` text NOT NULL, `slot_17` text NOT NULL, `slot_18` text NOT NULL, `slot_19` text NOT NULL, `slot_20` text NOT NULL, `slot_21` text NOT NULL, `slot_22` text NOT NULL, `slot_23` text NOT NULL, `slot_24` text NOT NULL, `slot_25` text NOT NULL, `slot_26` text NOT NULL, `slot_27` text NOT NULL, `slot_28` text NOT NULL, `slot_29` text NOT NULL, `slot_30` text NOT NULL, PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1
CREATE TABLE IF NOT EXISTS `guestbook_fields` (`id` int(11) NOT NULL auto_increment, `type_id` int(11) NOT NULL, `name` varchar(100) NOT NULL COMMENT 'name (label) of the field displayed', `slot` int(11) NOT NULL COMMENT 'slot number the field information can be found within', `position` int(11) NOT NULL COMMENT 'the position order the field should be displayed in', `required` int(1) NOT NULL default '0' COMMENT 'required to be filled out to create an account', `locked` int(1) NOT NULL default '0' COMMENT 'field cannot be removed', `show_on_registration` int(1) NOT NULL default '1' COMMENT 'display on registration component', `input_type` varchar(50) NOT NULL COMMENT 'how the information should be input (i.e. select, text, number)', `input_options` text NOT NULL COMMENT 'any additional information that is needed for a given field type (i.e. list options for option list)', `regex` text NOT NULL COMMENT 'regular expression to match input on', PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1
CREATE TABLE IF NOT EXISTS `guestbook_types` (`id` int(11) NOT NULL auto_increment, `site_id` int(11) NOT NULL, `name` varchar(100) NOT NULL, `available_slots` text NOT NULL, `restrict_access` int(1) NOT NULL default '0', `include_form_info` int(1) NOT NULL default '0', `show_registration` int(1) NOT NULL default '0', `default_group` int(11) NOT NULL, `confirmation_type` int(1) NOT NULL default '1'  COMMENT '1 = text message, 2 = redirect to url', `confirmation_value` text NOT NULL, `notification_email` text NOT NULL, `send_notification` int(1) NOT NULL default '1', PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1
