CREATE TABLE IF NOT EXISTS `newsletter_subscribers` (`id` int(11) NOT NULL auto_increment, `account_id` int(11) NOT NULL, `subscription_list_id` int(11) NOT NULL, PRIMARY KEY  (`id`))
CREATE TABLE IF NOT EXISTS `newsletter_subscription_lists` (`id` int(11) NOT NULL auto_increment, `site_id` int(11) NOT NULL, `name` text NOT NULL, `availability` int(11) NOT NULL default '0' COMMENT '0 = public, 1 = logged in account holders only, 2 = private, only administrators can add subscribers to the list', PRIMARY KEY  (`id`, `site_id`));
CREATE TABLE IF NOT EXISTS `newsletter_messages` (`id` int(11) NOT NULL auto_increment, `site_id` int(11) NOT NULL, `layout` text NOT NULL, `title` varchar(255) NOT NULL, `summary` text NOT NULL, `status` int(1) NOT NULL default '0', `message` text NOT NULL, `send_count` int(11) NOT NULL default '0', PRIMARY KEY  (`id`, `site_id`))
CREATE TABLE IF NOT EXISTS `newsletter_settings` (`id` int(11) NOT NULL auto_increment, `site_id` int(11) NOT NULL, `default_account_group` int(11) NOT NULL COMMENT 'the account group that new subscribers will be created within', `header` text NOT NULL, `footer` text NOT NULL, `smtp_host` varchar(200) NOT NULL, `smtp_user` varchar(200) NOT NULL, `smtp_password` varchar(200) NOT NULL, `smtp_port` varchar(10) NOT NULL, `rss_title` text NOT NULL, `rss_description` text NOT NULL, PRIMARY KEY  (`id`, `site_id`))
INSERT INTO `newsletter_settings` (`site_id`, `default_account_group`, `header`, `footer`, `smtp_host`, `smtp_user`, `smtp_password`, `smtp_port`, `rss_title`, `rss_description`) VALUES (NUMO_SITE_ID, 1, '<h1 style="padding:0px 5px;margin:0px;font-size: 20px;">Company Name</h1><p style="padding:3px 8px;margin:0px;font-style:italic;">Latest Company News</p>', '<P align=center>Copyright &copy; 20xx Company Name Here</P>', '', '', '', '', 'COMPANY NAME Newsletter', 'News and information about COMPANY NAME');
